// ========================================================================
// REDA EMC Testing Tool - Folder Browser Component
// ========================================================================
// Turner Engineering Corporation - Professional EMC Testing Support
// 
// Folder navigation component for browsing and selecting files
// ========================================================================

import React, { useState, useCallback } from 'react';
import { CsvService } from '../../services/CsvService';
import { NoiseAnalysisService, NoiseAnalysisResult } from '../../services/NoiseAnalysisService';
import { BandType, CsvDataPoint } from '../../types';
import { BAND_DEFINITIONS } from '../../constants';
import './FolderBrowser.css';

// ========================================================================
// INTERFACES
// ========================================================================

interface FolderItem {
  name: string;
  path: string;
  type: 'file' | 'folder';
  size?: number;
  modified?: Date;
  isCSV?: boolean;
  detectedBand?: BandType | null;
  bandConfidence?: 'high' | 'medium' | 'low';
  fileHandle?: any; // File handle for File System Access API
  noisinessAnalysis?: NoiseAnalysisResult; // Noisiness analysis data
}

interface FolderBrowserProps {
  panelId: string;
  title: string;
  maxFiles?: number;
  onFilesSelected?: (files: FolderItem[]) => void;
}

// ========================================================================
// FOLDER BROWSER COMPONENT
// ========================================================================

export const FolderBrowser: React.FC<FolderBrowserProps> = ({
  panelId,
  title,
  maxFiles = 8,
  onFilesSelected
}) => {
  const [currentPath, setCurrentPath] = useState<string>('');
  const [folderContents, setFolderContents] = useState<FolderItem[]>([]);
  const [filteredContents, setFilteredContents] = useState<FolderItem[]>([]);
  const [selectedFiles, setSelectedFiles] = useState<Set<string>>(new Set());
  const [selectedBandFilter, setSelectedBandFilter] = useState<BandType | 'all'>('all');
  const [, setSelectedBands] = useState<Set<BandType>>(new Set());
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string>('');
  const [pathHistory, setPathHistory] = useState<string[]>([]);
  const [sortBy, setSortBy] = useState<'name' | 'noisiness' | 'band'>('noisiness');
  const [isAnalyzingNoise, setIsAnalyzingNoise] = useState(false);

  // ========================================================================
  // NOISINESS ANALYSIS
  // ========================================================================

  const batchAnalyzeNoisiness = useCallback(async (items: FolderItem[]) => {
    const csvFiles = items.filter(item => item.isCSV && item.fileHandle);
    if (csvFiles.length === 0) return;

    setIsAnalyzingNoise(true);
    console.log(`üîä Analyzing band-relative noisiness for ${csvFiles.length} CSV files...`);

    // Parse all CSV files first
    const filesData: Array<{ filename: string; data: CsvDataPoint[]; band?: string; item: FolderItem }> = [];

    for (const item of csvFiles) {
      try {
        const file = await item.fileHandle!.getFile();
        const parseResult = await CsvService.parseCsvFile(file);

        if (parseResult.success && parseResult.data) {
          // Detect band from frequency data if not already detected
          let detectedBand = item.detectedBand;
          if (!detectedBand) {
            const frequencies = parseResult.data.map(point => point.frequency / 1e6);
            const minFreq = Math.min(...frequencies);
            const maxFreq = Math.max(...frequencies);
            detectedBand = CsvService.detectBandFromFrequencyRange(minFreq, maxFreq);
          }

          filesData.push({
            filename: item.name,
            data: parseResult.data,
            band: detectedBand || 'Unknown',
            item
          });
        }
      } catch (error) {
        console.warn(`Failed to parse ${item.name}:`, error);
      }
    }

    if (filesData.length === 0) {
      setIsAnalyzingNoise(false);
      return;
    }

    // Use band-relative noisiness analysis
    const analyses = NoiseAnalysisService.calculateBandRelativeNoisiness(filesData);

    // Update items with analysis results
    const updatedItems = [...items];
    analyses.forEach(analysis => {
      const fileData = filesData.find(fd => fd.filename === analysis.filename);
      if (fileData) {
        const itemIndex = updatedItems.findIndex(ui => ui.path === fileData.item.path);
        if (itemIndex >= 0) {
          updatedItems[itemIndex] = { 
            ...updatedItems[itemIndex], 
            noisinessAnalysis: analysis,
            detectedBand: fileData.band as any // Update band if it was detected
          };
        }
      }
    });

    setFolderContents(updatedItems);
    setIsAnalyzingNoise(false);

    console.log(`‚úÖ Band-relative noisiness analysis complete for ${csvFiles.length} files`);
    console.log(`üìä Results: Files now ranked 1-10 within their respective bands (1=quietest, 10=noisiest)`);
  }, []);

  // ========================================================================
  // FILTERING AND BAND DETECTION
  // ========================================================================

  const applyFilters = useCallback((items: FolderItem[]) => {
    let filtered = [...items];

    // Filter by band if not 'all'
    if (selectedBandFilter !== 'all') {
      filtered = filtered.filter(item => 
        item.type === 'folder' || 
        (item.isCSV && item.detectedBand === selectedBandFilter)
      );
    }

    // Sort items
    filtered.sort((a, b) => {
      // Always put folders first
      if (a.type !== b.type) {
        return a.type === 'folder' ? -1 : 1;
      }

      // For files, sort by selected criteria
      if (a.type === 'file' && b.type === 'file') {
        switch (sortBy) {
          case 'noisiness':
            const aNoise = a.noisinessAnalysis?.noisinessIndex ?? -1;
            const bNoise = b.noisinessAnalysis?.noisinessIndex ?? -1;
            // Sort by noisiness descending (noisiest first), then by name
            if (aNoise !== bNoise) {
              return bNoise - aNoise;
            }
            break;
          case 'band':
            const aBand = a.detectedBand || 'ZZ';
            const bBand = b.detectedBand || 'ZZ';
            if (aBand !== bBand) {
              return aBand.localeCompare(bBand);
            }
            break;
          case 'name':
          default:
            // Fall through to name sorting
            break;
        }
      }

      // Default: sort by name
      return a.name.localeCompare(b.name);
    });

    setFilteredContents(filtered);
  }, [selectedBandFilter, sortBy]);

  // Apply filters whenever band filter, sort option, or folder contents change
  React.useEffect(() => {
    applyFilters(folderContents);
  }, [folderContents, selectedBandFilter, sortBy, applyFilters]);

  // ========================================================================
  // FOLDER NAVIGATION
  // ========================================================================

  const loadFolderContents = useCallback(async (dirHandle: any) => {
    setIsLoading(true);
    setError('');
    const items: FolderItem[] = [];

    try {
      for await (const [name, handle] of dirHandle.entries()) {
        const isFile = handle.kind === 'file';
        const isCSV = isFile ? CsvService.isValidCsvFile({ name } as File) : false;

        // Only include CSV files
        if (isFile && !isCSV) {
          continue;
        }

        const item: FolderItem = {
          name,
          path: `${currentPath}/${name}`,
          type: isFile ? 'file' : 'folder',
          isCSV,
          fileHandle: isFile ? handle : undefined // Store the file handle for later access
        };

        if (isFile) {
          try {
            const file = await handle.getFile();
            item.size = file.size;
            item.modified = new Date(file.lastModified);

            // Detect band for CSV files
            if (item.isCSV) {
              const bandFromFilename = CsvService.detectBandFromFilename(name);
              if (bandFromFilename) {
                item.detectedBand = bandFromFilename;
                item.bandConfidence = 'high';
              } else {
                // Just detect from filename for now to avoid parsing all files
                item.detectedBand = null;
                item.bandConfidence = 'low';
              }
            }
          } catch (err) {
            // Skip files we can't access
            continue;
          }
        }

        items.push(item);
      }

      // Sort: folders first, then files, alphabetically
      items.sort((a, b) => {
        if (a.type !== b.type) {
          return a.type === 'folder' ? -1 : 1;
        }
        return a.name.localeCompare(b.name);
      });

      setFolderContents(items);

      // Apply filters inline to avoid dependency issues
      let filtered = [...items];
      if (selectedBandFilter !== 'all') {
        filtered = filtered.filter(item => 
          item.type === 'folder' || 
          (item.isCSV && item.detectedBand === selectedBandFilter)
        );
      }
      setFilteredContents(filtered);

      // Start noisiness analysis for CSV files in the background
      setTimeout(() => batchAnalyzeNoisiness(items), 100);
    } catch (error) {
      setError('Failed to read folder contents.');
    } finally {
      setIsLoading(false);
    }
  }, [currentPath, selectedBandFilter, batchAnalyzeNoisiness]);

  const loadFolderContentsFromFiles = useCallback((files: FileList) => {
    setIsLoading(true);
    setError('');
    const items: FolderItem[] = [];
    const seenPaths = new Set<string>();

    for (let i = 0; i < files.length; i++) {
      const file = files[i];
      const pathParts = file.webkitRelativePath.split('/');

      // Add folders in the path
      for (let j = 1; j < pathParts.length - 1; j++) {
        const folderPath = pathParts.slice(0, j + 1).join('/');
        if (!seenPaths.has(folderPath)) {
          seenPaths.add(folderPath);
          items.push({
            name: pathParts[j],
            path: folderPath,
            type: 'folder'
          });
        }
      }

      // Add the file
      if (!seenPaths.has(file.webkitRelativePath)) {
        seenPaths.add(file.webkitRelativePath);
        const isCSV = CsvService.isValidCsvFile(file);

        // Only include CSV files
        if (!isCSV) {
          continue;
        }

        const item: FolderItem = {
          name: file.name,
          path: file.webkitRelativePath,
          type: 'file',
          size: file.size,
          modified: new Date(file.lastModified),
          isCSV
        };

        // Detect band for CSV files
        if (isCSV) {
          const bandFromFilename = CsvService.detectBandFromFilename(file.name);
          if (bandFromFilename) {
            item.detectedBand = bandFromFilename;
            item.bandConfidence = 'high';
          } else {
            item.detectedBand = null;
            item.bandConfidence = 'low';
          }
        }

        items.push(item);
      }
    }

    // Filter to show only items in current directory level
    const currentLevel = currentPath.split('/').length;
    const currentLevelItems = items.filter(item => {
      const itemLevel = item.path.split('/').length;
      return itemLevel === currentLevel + 1 && item.path.startsWith(currentPath);
    });

    setFolderContents(currentLevelItems);

    // Apply filters inline to avoid dependency issues
    let filtered = [...currentLevelItems];
    if (selectedBandFilter !== 'all') {
      filtered = filtered.filter(item => 
        item.type === 'folder' || 
        (item.isCSV && item.detectedBand === selectedBandFilter)
      );
    }
    setFilteredContents(filtered);
    setIsLoading(false);

    // Note: Noisiness analysis not available for fallback file loading
    // since we don't have direct file handles in this mode
  }, [currentPath, selectedBandFilter]);

  const openFolderDialog = useCallback(async () => {
    try {
      // Use the File System Access API for modern browsers
      if ('showDirectoryPicker' in window) {
        const dirHandle = await (window as any).showDirectoryPicker();
        const path = dirHandle.name;
        setCurrentPath(path);
        await loadFolderContents(dirHandle);
      } else {
        // Fallback: use input element
        const input = document.createElement('input');
        input.type = 'file';
        input.webkitdirectory = true;
        input.multiple = true;

        input.onchange = (e) => {
          const files = (e.target as HTMLInputElement).files;
          if (files && files.length > 0) {
            const firstFile = files[0];
            const pathParts = firstFile.webkitRelativePath.split('/');
            const folderName = pathParts[0];
            setCurrentPath(folderName);
            loadFolderContentsFromFiles(files);
          }
        };

        input.click();
      }
    } catch (error) {
      if ((error as Error).name !== 'AbortError') {
        setError('Failed to open folder. Please try again.');
      }
    }
  }, [loadFolderContents, loadFolderContentsFromFiles]);

  // ========================================================================
  // FILE SELECTION
  // ========================================================================

    const toggleFileSelection = useCallback((path: string, itemBand?: BandType | null) => {
    setSelectedFiles(prev => {
      const newSelection = new Set<string>();
      let updatedBands = new Set<BandType>();

      if (prev.has(path)) {
        // Deselecting the currently selected file - clear everything
        newSelection.clear();
        updatedBands.clear();
            setSelectedBands(updatedBands);
        console.log(`üóëÔ∏è Deselected file: ${path}`);
      } else {
        // Selecting a new file - replace any previous selection
        newSelection.clear(); // Clear any previous selections
        newSelection.add(path); // Add only the new file

        // Update bands for the new selection
        updatedBands.clear();
        if (itemBand) {
          updatedBands.add(itemBand);
          setSelectedBands(updatedBands);
          console.log(`üîÑ Selected new file: ${path} (${itemBand} band) - replacing any previous selection`);
        } else {
          setSelectedBands(updatedBands);
          console.log(`üîÑ Selected new file: ${path} (Unknown band) - replacing any previous selection`);
        }

        setError(''); // Clear any previous errors
      }

      // Always trigger file loading when selection changes (including when clearing)
      setTimeout(() => {
        const selectedItems = filteredContents.filter(item => 
          newSelection.has(item.path) && item.type === 'file' && item.isCSV
        );

        // Always call the callback, even when no files are selected (to clear the plot)
        if (onFilesSelected) {
          onFilesSelected(selectedItems);
        }
      }, 100); // Small delay to ensure state updates are complete

      return newSelection;
    });
  }, [filteredContents, onFilesSelected]);

  // ========================================================================
  // NAVIGATION
  // ========================================================================

  const navigateToFolder = useCallback((folderPath: string) => {
    setPathHistory(prev => [...prev, currentPath]);
    setCurrentPath(folderPath);
    setSelectedFiles(new Set());
    setSelectedBands(new Set());
    setError('');
  }, [currentPath]);

  const navigateBack = useCallback(() => {
    if (pathHistory.length > 0) {
      const previousPath = pathHistory[pathHistory.length - 1];
      setPathHistory(prev => prev.slice(0, -1));
      setCurrentPath(previousPath);
      setSelectedFiles(new Set());
    }
  }, [pathHistory]);

  const navigateToRoot = useCallback(() => {
    setCurrentPath('');
    setPathHistory([]);
    setFolderContents([]);
    setSelectedFiles(new Set());
  }, []);

  // ========================================================================
  // CLEAR SELECTION
  // ========================================================================

  const clearSelection = useCallback(() => {
    setSelectedFiles(new Set());
    setSelectedBands(new Set());
    setError('');

    // Notify parent that no files are selected
    if (onFilesSelected) {
      onFilesSelected([]);
    }
  }, [onFilesSelected]);

  // ========================================================================
  // RENDER HELPERS
  // ========================================================================

  const renderPathBreadcrumb = () => {
    if (!currentPath) return null;

    const pathParts = currentPath.split('/');

    return (
      <div className="path-breadcrumb">
        <button onClick={navigateToRoot} className="breadcrumb-item root">
          Home
        </button>
        {pathParts.map((part, index) => (
          <React.Fragment key={index}>
            <span className="breadcrumb-separator">/</span>
            <span className="breadcrumb-item">{part}</span>
          </React.Fragment>
        ))}
      </div>
    );
  };

  const renderFolderItem = (item: FolderItem) => {
    const isSelected = selectedFiles.has(item.path);
    const isSelectable = item.type === 'file' && item.isCSV;

    return (
      <div
        key={item.path}
        className={`folder-item ${item.type} ${isSelected ? 'selected' : ''} ${isSelectable ? 'selectable' : ''}`}
        onClick={() => {
          if (item.type === 'folder') {
            navigateToFolder(item.path);
          } else if (isSelectable) {
            toggleFileSelection(item.path, item.detectedBand);
          }
        }}
      >
        <div className="item-icon">
          {item.type === 'folder' ? 'üìÅ' : item.isCSV ? 'üìà' : 'üìÑ'}
        </div>
        <div className="item-details">
          <div className="item-name">{item.name}</div>
          {item.type === 'file' && item.isCSV && (
            <div className="item-meta">
              <div className="band-info">
                {item.detectedBand && (
                  <span className={`band-indicator ${item.bandConfidence}`}>
                    {item.detectedBand}
                  </span>
                )}
                {!item.detectedBand && (
                  <span className="band-indicator unknown">
                    Unknown Band
                  </span>
                )}
              </div>
              {item.noisinessAnalysis && (
                <div className="noisiness-info">
                  <span 
                    className="noisiness-indicator"
                    style={{ color: NoiseAnalysisService.getNoisinessColor(item.noisinessAnalysis.noisinessIndex) }}
                    title={`Noisiness: ${item.noisinessAnalysis.noisinessIndex}/10 (${NoiseAnalysisService.getNoisinessDescription(item.noisinessAnalysis.noisinessIndex)})`}
                  >
                    {NoiseAnalysisService.getNoisinessEmoji(item.noisinessAnalysis.noisinessIndex)} {item.noisinessAnalysis.noisinessIndex}
                  </span>
                </div>
              )}
              {!item.noisinessAnalysis && isAnalyzingNoise && (
                <div className="noisiness-info">
                  <span className="noisiness-loading">üîÑ ...</span>
                </div>
              )}
            </div>
          )}
        </div>
        {isSelectable && (
          <div className="selection-indicator">
            <input 
              type="checkbox" 
              checked={isSelected} 
              onChange={() => toggleFileSelection(item.path, item.detectedBand)}
              onClick={(e) => e.stopPropagation()}
            />
          </div>
        )}
      </div>
    );
  };

  // ========================================================================
  // MAIN RENDER
  // ========================================================================

  return (
    <div className="folder-browser" data-panel-id={panelId}>
      <div className="browser-header">
        <h3 
          onClick={!isLoading ? openFolderDialog : undefined}
          style={{ 
            cursor: !isLoading ? 'pointer' : 'default',
            userSelect: 'none',
            color: !currentPath ? '#3498db' : 'inherit'
          }}
          title={!currentPath ? 'Click to open folder' : `Click to change folder (${currentPath})`}
        >
          {title}
          {currentPath ? ` - ${currentPath}` : ' (Click here to open folder)'}
        </h3>
        <div className="browser-controls">
          <button 
            onClick={openFolderDialog}
            className="open-folder-btn"
            disabled={isLoading}
          >
            {currentPath ? 'Change Folder' : 'Open Folder'}
          </button>
          {pathHistory.length > 0 && (
            <button onClick={navigateBack} className="back-btn">
              Back
            </button>
          )}
        </div>
      </div>

              {error && (
          <div className="error-message">
            {error}
          </div>
        )}

      {currentPath && (
        <>
          {renderPathBreadcrumb()}

          {/* Filter and Sort Controls */}
          <div className="filter-controls">
            <div className="filter-section">
              <label htmlFor={`band-filter-${panelId}`} className="filter-label">
                Band Filter:
              </label>
              <select
                id={`band-filter-${panelId}`}
                value={selectedBandFilter}
                onChange={(e) => {
                  setSelectedBandFilter(e.target.value as BandType | 'all');
                  applyFilters(folderContents);
                }}
                className="band-filter-select"
              >
                <option value="all">All Bands</option>
                {Object.entries(BAND_DEFINITIONS).map(([bandType, definition]) => {
                  const count = folderContents.filter(item => item.detectedBand === bandType).length;
                  return (
                    <option key={bandType} value={bandType}>
                      {bandType}: {definition.range} ({count} files)
                    </option>
                  );
                })}
              </select>
            </div>
            <div className="filter-section">
              <label htmlFor={`sort-filter-${panelId}`} className="filter-label">
                Sort by:
              </label>
              <select
                id={`sort-filter-${panelId}`}
                value={sortBy}
                onChange={(e) => {
                  setSortBy(e.target.value as 'name' | 'noisiness' | 'band');
                  applyFilters(folderContents);
                }}
                className="sort-filter-select"
              >
                <option value="noisiness">üîä Noisiness (High‚ÜíLow)</option>
                <option value="band">üìä Band</option>
                <option value="name">üìù Name</option>
              </select>
            </div>
            <div className="action-section">
              {isAnalyzingNoise && (
                <span className="analysis-status">
                  üîÑ Analyzing...
                </span>
              )}
              <button 
                onClick={clearSelection}
                disabled={selectedFiles.size === 0}
                className="clear-all-btn"
              >
                {selectedFiles.size > 0 ? 'Clear Selection' : 'Clear All'}
              </button>
            </div>
          </div>
        </>
      )}

      <div className="folder-contents">
        {!currentPath ? (
          <div className="empty-state">
            <div className="empty-icon">üìÇ</div>
            <p>Click "Open Folder" to browse and select CSV files</p>
          </div>
        ) : isLoading ? (
          <div className="loading-state">
            <div className="spinner">‚ü≥</div>
            <p>Loading folder contents...</p>
          </div>
        ) : filteredContents.length === 0 ? (
          <div className="empty-folder">
            <div className="empty-icon">üìÇ</div>
            <p>{selectedBandFilter !== 'all' ? `No CSV files found for band ${selectedBandFilter}` : 'This folder is empty'}</p>
            {selectedBandFilter !== 'all' && folderContents.length > 0 && (
              <small>Try selecting "All Bands" to see all files</small>
            )}
          </div>
        ) : (
          <div className="items-list">
            {filteredContents.map(renderFolderItem)}
          </div>
        )}
      </div>
    </div>
  );
};

export default FolderBrowser;
